import styled from 'styled-components';

export const ButtonCalculator = styled.button({

    fontSize: '3rem',
    padding: '0 10px',
    border: '1px solid white',
    backgroundColor: '#0dbde5',
    color: 'white',
    fontWeight: "700",
    flex: 1,

    "&:hover": {
        opacity: "0.6"
    }

})
