import styled from 'styled-components'


export const InputContainer = styled.div `
    width: 100%;
    max-width: 620px;
    height: 30px;
    border-bottom: 1px solid #381450;

    display: flex;
    align-items: center;
    margin-bottom: 30px;

    & p {
        color: red;
        font-size: 12px;
    }

`
export const ErrorInput = styled.p `
    color: red;
    font-size: 12px;
    margin-left: 1rem;
`

export const IconContainer = styled.div `
    margin-right: 10px
`

export const InputText = styled.input`
    background: transparent;
    color: #ffffff;
    border: 0;
    height: 30px;
    width: 100%;



`
